    const router = useRouter()
    const pathname = usePathname()

    // const [isDotChecked, setIDotisDotChecked] = useState(false);

    const topAllowedAttrs = {
        top: ['car', 'season'],
        bottom: ['width', 'height', 'zoll']
    }
   
